mapper.select:
 		select *
		from expert_review e
		where e.sell_num IN(
         		           	select p.sell_num
                		    from user_table u, payment p
 							where u.user_id = p.user_id
                    		and u.user_id = #{user_id}
                    	)
        and e.expert_review_num = #{expert_review_num}

select *
from expert_review
where user_id =#{user_id}
and expert_review_num=#{expert_review_num}
